r) { res = [] words = str.split(&#39; &#39;) for(i=0;i&lt;words.length;i++) { if(!stopWords.includes(words[i])) { res.push(words[i]) } } return(res.join(&#39; &#39;)) } //Simple animated example of d3-cloud - https://github.com/jasondavies/d3-cloud //Based on https://github.com/jasondavies/d3-cloud/blob/master/examples/simple.html // Encapsulate the word cloud functionality function wordCloud(selector) { var fill = d3.scale.category20(); //Construct the word cloud&#39;s SVG element var svg = d3.select(selector).append(&#34;svg&#34;) .attr(&#34;viewBox&#34;, `0 0 800 800`) .append(&#34;g&#34;) .attr(&#34;transform&#34;, &#34;translate(400,400)&#34;); //Draw the word cloud function draw(words) { var cloud = svg.selectAll(&#34;g text&#34;) .data(words, function(d) { return d.text; }) //Entering words cloud.enter() .append(&#34;text&#34;) .style(&#34;font-family&#34;, &#34;Impact&#34;) .style(&#34;fill&#34;, function(d, i) { return fill(i); }) .attr(&#34;text-anchor&#34;, &#34;middle&#34;) .attr(&#39;font-size&#39;, 1) .text(function(d) { return d.text; }); //Entering and existing words cloud .transition() .duration(600) .style(&#34;font-size&#34;, function(d) { return d.size + &#34;px&#34;; }) .attr(&#34;transform&#34;, function(d) { return &#34;translate(&#34; + [d.x, d.y] + &#34;)rotate(&#34; + d.rotate + &#34;)&#34;; }) .style(&#34;fill-opacity&#34;, 1); //Exiting words cloud.exit() .transition() .duration(200) .style(&#39;fill-opacity&#39;, 1e-6) .attr(&#39;font-size&#39;, 1) .remove(); } //Use the module pattern to encapsulate the visualisation code. We&#39;ll // expose only the parts that need to be public. return { //Recompute the word cloud for a new set of words. This method will // asycnhronously call draw when the layout has been computed. //The outside world will need to call this function, so make it part // of the wordCloud return value. update: function(words) { d3.layout.cloud().size([800, 800]) .words(words) .padding(5) .rotate(function() { return ~~(Math.random() * 2) * 90; }) .font(&#34;Impact&#34;) .fontSize(function(d) { return d.size; }) .on(&#34;end&#34;, draw) .start(); } } } //Prepare one of the sample sentences by removing punctuation, // creating an array of words and computing a random size attribute. function getWords(i) { arr = words.split(&#34; &#34;); let freq = calculateFrequency(arr); return buildResult(arr); } //This method tells the word cloud to redraw with a new set of words. //In reality the new words would probably come from a server request, // user input or some other source. function showNewWords(vis, i) { i = i || 0; vis.update(getWords(i ++ % words.length)) } //Create a new instance of the word cloud visualisation. var myWordCloud = wordCloud(&#39;#cloud&#39;); function calculateFrequency(arr) { var a = [], b = [], prev; arr.sort(); for ( var i = 0; i &lt; arr.length; i++ ) { if ( arr[i] !== prev ) { a.push(arr[i]); b.push(1); } else { b[b.length-1]++; } prev = arr[i]; } return [a,b]; } function buildResult(arr){ let resultArr = []; let sum = 0; let total = freq[0].length; for(let i = 0; i &lt; total; i++) resultArr.push({ text: freq[0][i], size: freq[1][i] }); let sorted = resultArr.sort( (a,b) =&gt; b.size - a.size); sorted = sorted.slice(0, 50); for(let i = 0; i &lt; sorted.length; i++) sum += sorted[i].size; resultArr = []; for(let i = 0; i &lt; sorted.length; i++) resultArr.push({ text: sorted[i][&#34;text&#34;], size: (sorted[i][&#34;size&#34;] / sum) * 60 + 50 }); return resultArr; } let getPost = () =&gt; { let result = &#34;&#34;; let endPoint = &#34;https://reddit.com/r/programming.json?limit=1000&amp;jsonp=?&#34; $.getJSON(endPoint, function(data){ result = data; let entries = result[&#34;data&#34;].children; for(let i = 0; i &lt; entries.length; i++){ let link = (entries[i][&#34;data&#34;][&#34;title&#34;]); words += &#34; &#34; + (entries[i][&#34;data&#34;][&#34;title&#34;]); } words = words.replace(/[^\w\s]/gi, &#39;&#39;); words = words.replace(/\d/g, &#39;&#39;); words = remove_stopwords(words.toLowerCase()); arr = words.split(&#34; &#34;); freq = calculateFrequency(arr); showNewWords(myWordCloud); }); } getPost();

The charts here are visualisation of the Am I the Asshole? subreddit which can be found here
The subreddit describes itself as&hellip;
A catharsis for the frustrated moral philosopher in all of us, and a place to finally find out if you were wrong in an argument that&rsquo;s been bothering you. Tell us about any non-violent conflict you have experienced; give us both sides of the story, and find out if you&rsquo;re right, or you&rsquo;re the asshole. See our Best Of &ldquo;Most Controversial&rdquo; at /r/AITAFiltered!
Please note that the posts here are generated based on the Reddit website by doing GET requests. So, it is based on their current entries. So, these are live data obtained from the site and not static data hardcoded in the website.
Abbreviation Meaning YTA You&rsquo;re the A-hole NTA Not the A-hole ESH Everyone sucks here NAH No A-holes here INFO Not Enough Info Fig 1. Abbreviation Table for the Subreddit Posts let summary = []; let parseResult = (link) =&gt; { const endPoint = &#34;https://reddit.com&#34; + link + &#34;.json?limit=80&amp;jsonp=?&#34;; let replies = &#34;&#34;; $.getJSON(endPoint, function(data){ let title = (data[0].data.children[0].data[&#34;title&#34;]); replies = data[1][&#34;data&#34;].children; let url = &#34;https://reddit.com&#34; + link; let noOfReplies = replies.length; let countNTAAppearance = 0; let nta = &#34;NTA&#34;; let countYTAAppearance = 0; let countESHAppearance = 0; let countNAHAppearance = 0; let countINFOAppearance = 0; for (let i = 0; i &lt; noOfReplies; i++) { let reply = replies[i][&#34;data&#34;].body; if (reply == undefined) return; countNTAAppearance += (reply.match(/NTA/g) || []).length; countYTAAppearance += (reply.match(/YTA/g) || []).length; countESHAppearance += (reply.match(/ESH/g) || []).length; countNAHAppearance += (reply.match(/NAH/g) || []).length; countINFOAppearance += (reply.match(/INFO/g) || []).length; } let jsonResult = { &#34;id&#34; : data[0].data.children[0].data[&#34;id&#34;], &#34;url&#34;: url, &#34;title&#34;: title, &#34;countNTAAppearance&#34;: countNTAAppearance, &#34;countYTAAppearance&#34;: countYTAAppearance, &#34;countESHAppearance&#34; : countESHAppearance, &#34;countNAHAppearance&#34; : countNAHAppearance, &#34;countINFOAppearance&#34; : countINFOAppearance, } summary.push(jsonResult); showResult(jsonResult); }); } let showResult = (jsonResult) =&gt; { let output = &#34;&lt;strong&gt;&#34; + jsonResult[&#34;title&#34;] + &#34;&lt;/strong&gt;&#34;; let out = output + &#34;&lt;p&gt;&lt;a id=&#34; + jsonResult[&#34;id&#34;] + &#34;_link&gt; Click here&lt;/a&gt; to view post in context.&lt;/p&gt;&#34;; $(&#34;.result&#34;).append(&#34;&lt;div class = &#39;shadow&#39;&gt;&#34; + out +&#34;&lt;div class=&#39;&#39; id=&#34; + jsonResult[&#34;id&#34;] + &#34;&gt;&lt;/div&gt;&lt;/div&gt;&#34;); $(&#34;#&#34; + jsonResult[&#34;id&#34;] + &#34;_link&#34;).prop(&#34;href&#34;, jsonResult[&#34;url&#34;]); $(&#34;.result&#34;).append(&#34;&lt;p&gt;&lt;/p&gt;&#34;); let id = &#34;#&#34; + jsonResult[&#34;id&#34;]; const data = { labels: [&#34;NTA&#34;,&#34;YTA&#34;,&#34;ESH&#34;,&#34;NAH&#34;,&#34;INFO&#34;], datasets: [ { name: &#34;data&#34;, charType: &#39;percentage&#39;, values: [ jsonResult[&#34;countNTAAppearance&#34;], jsonResult[&#34;countYTAAppearance&#34;], jsonResult[&#34;countESHAppearance&#34;], jsonResult[&#34;countNAHAppearance&#34;], jsonResult[&#34;countINFOAppearance&#34;], ] } ] } const chart = new frappe.Chart(id, { data: data, type: &#39;percentage&#39;, colors: [&#39;#33691e&#39;, &#39;#b71c1c&#39;, &#39;#f47e17&#39;,&#39;#1a237e&#39;,&#39;#e8eaf6&#39;] }) } let getPost = () =&gt; { let result = &#34;&#34;; let entries = []; let endPoint = &#34;https://reddit.com/r/amitheasshole.json?limit=50&amp;jsonp=?&#34; $.getJSON(endPoint, function(data){ result = data; entries = result[&#34;data&#34;].children; for(let i = 0; i &lt; entries.length; i++){ let link = (entries[i][&#34;data&#34;][&#34;permalink&#34;]); parseResult(link) } }); } getPost();

