function getSearchJSON(){$.getJSON("/search.json",function(e){console.log("[search.json loaded for instant search]");$("#search_results").html("");searchJSON=e})}function doSearch(e){results=[];if(e!=""){$.each(searchJSON,function(t,n){var r=n.title,i=n.title.toLowerCase(),s=n.href,o=n.date;i.indexOf(e)!==-1&&results.push([r,s,o])});printResults()}else{$("#search_results").html();results=[];printResults()}}function printResults(){var e=$("#search_results");e.html("");e.html(function(){if(results.length==0){e.append('<li style="padding-top: 3px; padding-bottom: 3px; list-style-type: none;">No results found.</li>')}else{$.each(results,function(t,n){e.append('<li style="padding-top: 3px; padding-bottom: 3px">'+n[2].day+" "+n[2].month+" "+n[2].year+" &raquo; "+'<a href="'+n[1]+'">'+n[0]+"</a></li>")})}})}function checkTextField(){if($("#search_input").val()!=""){$("#search_results").show()}else{$("#search_results").hide()}}$(document).mouseup(function(e){var t=$("#search_results");if(!t.is(e.target)&&t.has(e.target).length===0){t.hide()}});$(document).ready(function(){getSearchJSON();$("#search_input").keyup(function(){var e=$(this).val().toLowerCase();doSearch(e)})})